def generate_prediction_name():
	temp = requests.get(url="https://moivedata-default-rtdb.firebaseio.com/history.json").json()
	#print(temp.keys())
	if temp == None:
		new_id = 1
	else:
		#name_id = list(temp.keys())[-1][-1]
		print(list(temp.keys()))
		id_lst = []
		for i in list(temp.keys()):
			d = i.split('_')[-1]
			id_lst.append(int(d))
		new_id = max(id_lst) + 1
	return "prediction_" + str(new_id) + ".json"

